import React, { useState } from 'react'
import TextField from '@mui/material/TextField';
import AccountCircle from '@mui/icons-material/AccountCircle';
import InputAdornment from '@mui/material/InputAdornment';
import { styled } from '@mui/material/styles'
import MenuItem from '@mui/material/MenuItem';

import { Categories } from '../../data'
import './style.css'

const CssTextField = styled(TextField)({
  '& label.Mui-focused': {
    color: 'var(--green)',
  },
  input: {
    color: 'black'
  },
  label: {
    color: 'black',
  },
  
  '& .MuiInput-underline:after': {
    borderBottomColor: 'var(--green)',
  },
  '& .MuiInput-underline:before': {
    borderBottomColor: 'white',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'white',
    },
    '&:hover fieldset': {
      borderColor: 'var(--lightBlue)',
    },
    '&.Mui-focused fieldset': {
      borderColor: 'var(--green)',
    },
  },
});
const Settings = () => {
  const [username,setUsername] = useState('')
  const [category,setCat] = useState('')
  const [difficulty,setDiff] = useState('')
  const [type,setType]= useState('')
  console.log(username,category,difficulty,type)

  const catVal = Categories.map((cat) => cat.value)
console.log(catVal)
  return (
    <div className='settings'>
      <h1>Questions</h1>

      <form>
        <CssTextField
          id="username"
          label="Username"
          margin="normal"
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <AccountCircle />
              </InputAdornment>
            ),
          }}
          variant="standard"
        />

        <CssTextField
          id="categories"
          select
          label="Select a category"
          margin="normal"
          value={category}
        >
          {
          Categories.map((cat) => (
            <MenuItem key={cat.category} value={cat.value}>
              {cat.category}
            </MenuItem>
            
          ))
          }

        </CssTextField>

        <CssTextField
          id="difficulties"
          select
          label="Select a difficulty"
          margin="normal"
          value={difficulty}

        >
          <MenuItem id='easy' key='Easy' value='easy'>Easy</MenuItem>
          <MenuItem key='Medium' value='medium'>Medium</MenuItem>
          <MenuItem key='Hard' value='hard'>Hard</MenuItem>
        </CssTextField>

        <CssTextField
          id="qtype"
          select
          label="Select a type"
          margin="normal"
          value={type}

        >
          <div id='menuitem'>
          <MenuItem key='Multiple Choice' value='multipleChoice'>Muiltple Choice</MenuItem>
          <MenuItem key='TrueFalse' value='trueFalse'>True/False</MenuItem>
          <MenuItem key='Combonation' value='combination'>Combination</MenuItem>
          </div>
        </CssTextField>

        <button>Start &#8594;</button>
        
      </form>
    </div>
  )
};
export default Settings
