import React, { useState } from 'react'
import TextField from '@mui/material/TextField';
import AccountCircle from '@mui/icons-material/AccountCircle';
import InputAdornment from '@mui/material/InputAdornment';
import { styled } from '@mui/material/styles'
import MenuItem from '@mui/material/MenuItem';

import { Categories } from '../../data'
import './style.css'

const CssTextField = styled(TextField)({
  '& label.Mui-focused': {
    color: '#5bba6f',
  },
  input: {
    color: 'white'
  },
  label: {
    color: 'white',
  },
  '& .MuiInput-underline:after': {
    borderBottomColor: '#5bba6f',
  },
  '& .MuiInput-underline:before': {
    borderBottomColor: 'white',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'white',
    },
    '&:hover fieldset': {
      borderColor: '#01c9df',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#5bba6f',
    },
  },
});
const Settings = () => {
  const [username,setUsername] = useState('')
  const [category,setCat] = useState('')
  const [difficulty,setDiff] = useState('')
  const [type,setType]= useState('')
  console.log(username,category,difficulty,type)

  return (
    <>
      <h1>Questions</h1>

      <form>
        <CssTextField
          id="username"
          label="Username"
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <AccountCircle />
              </InputAdornment>
            ),
          }}
          variant="standard"
          margin="normal"
        />

        <CssTextField
          id="categories"
          select
          label="Select a category"
          value={category}
        >
          {
          Categories.map((cat) => (
            <MenuItem key={cat.category} value={cat.category}>
              {cat.value}
            </MenuItem>
          ))
          }

        </CssTextField>

        <CssTextField
          id="difficulties"
          select
          label="Select a difficulty"
          value={difficulty}

        >
          <MenuItem key='Easy' value='easy'>Easy</MenuItem>
          <MenuItem key='Medium' value='medium'>Medium</MenuItem>
          <MenuItem key='Hard' value='hard'>Hard</MenuItem>
        </CssTextField>

        <CssTextField
          id="qtype"
          select
          label="Select a type"
          margin="normal"
          value={'type'}

        >
          <MenuItem key='Multiple Choice' value='multipleChoice'>Muiltple Choice</MenuItem>
          <MenuItem key='TrueFalse' value='trueFalse'>True/False</MenuItem>
          <MenuItem key='Combonation' value='combination'>Combination</MenuItem>
        </CssTextField>

        <button>Start &#8594;</button>
        
      </form>
    </>
  )
};
export default Settings
